Joining for Interactive Polygons

states &lt;- states |&gt;
  filter(!(name %in% c("Alaska", "Hawaii", "Puerto Rico"))) |&gt;
  select("name", "geometry")

frogs_interactive &lt;- frogs |&gt;
  right_join(states, by = c("state" = "name"))


forest_interactive &lt;- forests |&gt;
  right_join(states, by = c("state" = "name"))
